import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import Apiservice, { marvelUrls } from "../ApiService/Apiservice";

const EventDetails = () => {
  const { id } = useParams();
  const [event, setEvent] = useState(null);
  const [comics, setComics] = useState([]);
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        const [eventResponse, comicsResponse, charactersResponse] =
          await Promise.all([
            Apiservice.GetEventById(id),
            Apiservices.GetEventComics(id),
            Apiservice.GetEventCharacters(id),
          ]);

        setEvent(eventResponse.data.data.results[0]);
        setComics(comicsResponse.data.data.results);
        setCharacters(charactersResponse.data.data.results);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchEventDetails();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="event-details">
      {event && (
        <>
          <h1>{event.title}</h1>
          <p>{event.description || "No description available"}</p>
          <h2>Comics</h2>
          <ul>
            {comics.map((comic) => (
              <li key={comic.id}>
                <Link to={`/comics/${comic.id}`}>{comic.title}</Link>
              </li>
            ))}
          </ul>
          <h2>Characters</h2>
          <ul>
            {characters.map((character) => (
              <li key={character.id}>
                <Link to={`/characters/${character.id}`}>{character.name}</Link>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default EventDetails;
