import axios from "axios";

class MarvelApiService {
  GetApiCall(url) {
    return axios.get(url);
  }

  GetCharacterById(id) {
    const url = `${marvelUrls.FETCH_CHARACTERS}/${id}?ts=1&apikey=2e1cdeec426ae323484f29024084c206&hash=d516513ba95b9407c7aca0f73b241f8a`;
    return axios.get(url);
  }

  GetComicById(id) {
    const url = `${marvelUrls.FETCH_COMICS}/${id}?ts=1&apikey=2e1cdeec426ae323484f29024084c206&hash=d516513ba95b9407c7aca0f73b241f8a`;
    return axios.get(url);
  }
}

export const marvelUrls = {
  FETCH_CHARACTERS: "https://gateway.marvel.com/v1/public/characters",
  FETCH_COMICS: "https://gateway.marvel.com/v1/public/comics",
};

export default new MarvelApiService();
