// src/components/ComicDetails.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const ComicDetails = () => {
  const { id } = useParams();
  const [comic, setComic] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchComicDetails = async () => {
      try {
        const response = await axios.get(
          `https://gateway.marvel.com/v1/public/comics/${id}`,
          {
            params: {
              apikey: process.env.REACT_APP_MARVEL_API_KEY, // Make sure to set your API key in the .env file
            },
          }
        );
        setComic(response.data.data.results[0]);
      } catch (err) {
        setError("Failed to fetch comic details");
      } finally {
        setLoading(false);
      }
    };

    fetchComicDetails();
  }, [id]);

  return (
    <div className="comic-details">
      {loading && <p>Loading...</p>}
      {error && <p className="error-message">{error}</p>}
      {comic && (
        <>
          <h1>{comic.title}</h1>
          <img
            src={`${comic.thumbnail.path}.${comic.thumbnail.extension}`}
            alt={comic.title}
          />
          <p>{comic.description}</p>
          <div className="comic-info">
            <h2>Characters:</h2>
            <ul>
              {comic.characters.items.map((character) => (
                <li key={character.name}>{character.name}</li>
              ))}
            </ul>
            <h2>Creators:</h2>
            <ul>
              {comic.creators.items.map((creator) => (
                <li key={creator.name}>
                  {creator.name} ({creator.role})
                </li>
              ))}
            </ul>
            <h2>Events:</h2>
            <ul>
              {comic.events.items.map((event) => (
                <li key={event.name}>{event.name}</li>
              ))}
            </ul>
          </div>
        </>
      )}
    </div>
  );
};

export default ComicDetails;
