import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./Marvel.css"; // Import your CSS file for styling

export const Marvel = () => {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchCharacter = async () => {
    try {
      setLoading(true);
      const res = await axios.get(
        `https://gateway.marvel.com:443/v1/public/characters/${id}?ts=1&apikey=2e1cdeec426ae323484f29024084c206&hash=d516513ba95b9407c7aca0f73b241f8a`
      );
      console.log("API Response:", res.data); // Log the entire API response
      setItem(res.data.data.results[0]);
    } catch (error) {
      console.error("Error fetching character data:", error);
      setError("Failed to load character details.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCharacter();
  }, [id]);

  if (loading) {
    return <p className="loading">Loading...</p>; // Loading state
  }

  if (error) {
    return <p className="error">{error}</p>; // Error state
  }

  if (!item) {
    return <p className="not-found">Character not found</p>;
  }

  return (
    <div className="marvel-container">
      <div className="image-box">
        <img
          src={`${item.thumbnail.path}.${item.thumbnail.extension}`}
          alt={item.name}
          className="character-image"
        />
      </div>
      <div className="details-box">
        <h1 className="character-name">{item.name}</h1>
        <p className="character-description">
          {item.description || "No description available."}
        </p>
        <div className="details-sections">
          <section className="details-section">
            <h2>Comics</h2>
            {item.comics.items.length ? (
              <ul>
                {item.comics.items.map((comic, index) => (
                  <li key={index}>{comic.name}</li>
                ))}
              </ul>
            ) : (
              <p>No comics available.</p>
            )}
          </section>
          <section className="details-section">
            <h2>Events</h2>
            {item.events.items.length ? (
              <ul>
                {item.events.items.map((event, index) => (
                  <li key={index}>{event.name}</li>
                ))}
              </ul>
            ) : (
              <p>No events available.</p>
            )}
          </section>
          <section className="details-section">
            <h2>Stories</h2>
            {item.stories.items.length ? (
              <ul>
                {item.stories.items.map((story, index) => (
                  <li key={index}>{story.name}</li>
                ))}
              </ul>
            ) : (
              <p>No stories available.</p>
            )}
          </section>
          <section className="details-section">
            <h2>Series</h2>
            {item.series.items.length ? (
              <ul>
                {item.series.items.map((series, index) => (
                  <li key={index}>{series.name}</li>
                ))}
              </ul>
            ) : (
              <p>No series available.</p>
            )}
          </section>
        </div>
      </div>
    </div>
  );
};
