import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Apiservice from "../ApiService/Apiservice"; // Adjust the import path if necessary

export const Marvel = () => {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false); // State to track favorite status

  const fetchCharacter = async () => {
    try {
      setLoading(true);
      const response = await Apiservice.GetCharacterById(id);
      console.log("API marvel data Response:", response);
      if (response.status === 200) {
        setItem(response.data.data.results[0]);
      } else {
        throw new Error(`API responded with status ${response.status}`);
      }
    } catch (error) {
      console.error("Error fetching character details:", error);
      setError(`Failed to load character details: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCharacter();
  }, [id]);

  const handleAddToFavorite = () => {
    setIsFavorite(!isFavorite); // Toggle favorite status
    // Implement logic to add or remove from favorites here
  };

  if (loading) {
    return <p className="BeforeLoading">Loading...</p>;
  }

  if (error) {
    return <p className="Error">{error}</p>;
  }

  return (
    <>
      {!item ? (
        <p className="NotFound">Character not found</p>
      ) : (
        <div className="box-content">
          <div className="right-box">
            <img
              src={`${item.thumbnail.path}.${item.thumbnail.extension}`}
              alt={item.name}
            />
          </div>
          <div className="left-box">
            <h1>{item.name}</h1>
            <h4>{item.description}</h4>
            <button className="favorite-button" onClick={handleAddToFavorite}>
              {isFavorite ? "❤️ Remove from Favorites" : "♡ Add to Favorites"}
            </button>
          </div>
        </div>
      )}
    </>
  );
};
